/************************************************************************************
 * @brief
 * 传入日期，得到今天都有哪些节日
 * 
 * @n<b>函数名称</b>                  : DateOfFestival
 * @n@param const DateInfo & l_date   : 传入的日期
 * @param   FtvList &        vecGlFtv : 公历国历节日
 * @param   FtvList &        vecNlFtv : 中国传统农历节日
 * @param   FtvList &        vecJqFtv : 二十四节气
 * @param   bool             bGLDate  : true，传入的日期为公历（默认），false：传入的日期为农历
 * @return                             
 * @see                                
 * @n<b>作者</b>                      :檀杨挺
 * @n<b>创建时间</b>                  : 2010-11-25 18:32:24
 * @version	修改者        时间        描述@n
 * @n		檀杨挺        2010-11-25 
 ************************************************************************************/
int Festival::DateOfFestivalForType(const int nType, const DateInfo& l_date, FtvList& vecGlFtv, FtvList& vecNlFtv, FtvList& vecJqFtv, bool bGLDate)
{
	vecGlFtv.clear();
	vecNlFtv.clear();
	vecJqFtv.clear();
	
	// 根据传入的日期，进行必要的公农历转换
	DateInfo l_glDate,l_nlDate;
	if (bGLDate)
	{
		l_glDate = l_date;
		l_nlDate = Calendar::Lunar(l_glDate);
	}
	else
	{
		l_nlDate = l_date;
		l_glDate = Calendar::GetGlDate(l_nlDate);
	}
	
	// 计算当天是公历月中存在什么节日
	int l_iArryNum = 0;
	int l_iMonthIdx = l_glDate.month - 1;
	std::string l_sMatchStr = "";
	char l_szM[8] = {0};
	if (l_iMonthIdx>=0 && l_iMonthIdx<12)
	{
		if (nType == 1)
			l_iArryNum = sizeof(g_comFtvDay_simple[l_iMonthIdx])/sizeof(std::string);
		else {
			l_iArryNum = sizeof(g_comFtvDay[l_iMonthIdx])/sizeof(std::string);
		}

		sprintf(l_szM, "%02d%02d", l_glDate.month, l_glDate.day);
		l_sMatchStr = l_szM;
	}
	
	// ======公历节日=======
	std::string l_sJieRi;
	std::string l_sTemp;
	std::vector<std::string> l_vecTemp;
	for (int i = 0; i < l_iArryNum; i++)
	{		
		if (nType == 1)
			l_sTemp = g_comFtvDay_simple[l_iMonthIdx][i];
		else
			l_sTemp = g_comFtvDay[l_iMonthIdx][i];
		if (l_sTemp.find(l_sMatchStr) != std::string::npos)
		{
			l_sJieRi = l_sTemp.substr(5, l_sTemp.length()-5);
			StringHelper::Tokenize(l_vecTemp, l_sJieRi, " ");
			vecGlFtv.assign(l_vecTemp.begin(), l_vecTemp.end());
			break;
		}
	}
	
	// 月周节日
	if (l_glDate.month==5 || l_glDate.month==6 || l_glDate.month==7 || l_glDate.month==9 || l_glDate.month==10 || l_glDate.month==11)
	{
		l_vecTemp.clear();
		if (nType == 1)
			l_iArryNum = sizeof(g_weekFtvDay_simple)/sizeof(std::string);
		else
			l_iArryNum = sizeof(g_weekFtvDay)/sizeof(std::string);
		// 求出这天是X月的第Y个星期z(0表示周日 5表示最后一个)
		int l_iWeekDay = 0;
		l_iWeekDay = Calendar::DayOfWeekFlag(l_glDate);
		int iWeekIdx = 0;
		iWeekIdx = (l_glDate.day - 1)/7 + 1;
		memset(l_szM, 0, 8);
		sprintf(l_szM, "%02d%d%d", l_glDate.month, iWeekIdx, l_iWeekDay);
		l_sMatchStr = l_szM;
		for (int i = 0; i < l_iArryNum; i++)
		{
			if (nType == 1)
				l_sTemp = g_weekFtvDay_simple[i];
			else
				l_sTemp = g_weekFtvDay[i];
			if (l_sTemp.find(l_sMatchStr) != std::string::npos)
			{
				l_sJieRi = l_sTemp.substr(5, l_sTemp.length()-5);
				StringHelper::Tokenize(l_vecTemp, l_sJieRi, " ");
				vecGlFtv.insert(vecGlFtv.end(), l_vecTemp.begin(), l_vecTemp.end());
				break;
			}
		}
	}
	
	// 月周节日2
	if (l_glDate.month==1 || l_glDate.month==9)
	{
		l_vecTemp.clear();
		if (nType == 1)
			l_iArryNum = sizeof(g_weekFtvDay2_simple)/sizeof(std::string);
		else
			l_iArryNum = sizeof(g_weekFtvDay2)/sizeof(std::string);
		// 求出这天是X月的第Y个星期z(0表示周日 5表示最后一个)
		int l_iTotalDay = GetGlMonthDays(l_glDate.year, l_glDate.month);
		int l_iWeekDay = Calendar::DayOfWeekFlag(l_glDate);
		bool l_bLastWeek = (l_glDate.day+7 > l_iTotalDay)?true:false;
		if (l_bLastWeek)
		{
			memset(l_szM, 0, 8);
			sprintf(l_szM, "%02d%d%d", l_glDate.month, 5, l_iWeekDay);
			l_sMatchStr = l_szM;
			for (int i = 0; i < l_iArryNum; i++)
			{
				if (nType == 1)
					l_sTemp = g_weekFtvDay2_simple[i];
				else					
					l_sTemp = g_weekFtvDay2[i];
				
				if (l_sTemp.find(l_sMatchStr) != std::string::npos)
				{
					l_sJieRi = l_sTemp.substr(5, l_sTemp.length()-5);
					StringHelper::Tokenize(l_vecTemp, l_sJieRi, " ");
					vecGlFtv.insert(vecGlFtv.end(), l_vecTemp.begin(), l_vecTemp.end());
					break;
				}
			}
		}
	}
	
	
	// =========农历节日===========
	if (nType == 1)
		l_iArryNum = sizeof(g_LunarFtvDay_simple)/sizeof(std::string);
	else
		l_iArryNum = sizeof(g_LunarFtvDay)/sizeof(std::string);
	memset(l_szM, 0, 8);
	sprintf(l_szM, "%02d%02d", l_nlDate.month, l_nlDate.day);
	l_sMatchStr = l_szM;
	for (int i = 0; i < l_iArryNum; i++)
	{
		if (nType == 1)
			l_sTemp = g_LunarFtvDay_simple[i];
		else
			l_sTemp = g_LunarFtvDay[i];
		if (l_sTemp.find(l_sMatchStr) != std::string::npos)
		{
			l_sJieRi = l_sTemp.substr(5, l_sTemp.length()-5);
			vecNlFtv.push_back(l_sJieRi);
			break;
		}
	}
	
	// 对除夕这天特别判断
	std::string l_sChuXi = "除夕";
	if (vecNlFtv.empty() && l_nlDate.month == 12 && (l_nlDate.day==29||l_nlDate.day==30))
	{
		if (Calendar::LeapMonth(l_nlDate.year) != 12)
		{
			if (l_nlDate.day == Calendar::MonthDaysFun(l_nlDate.year,l_nlDate.month))
			{
				vecNlFtv.push_back(l_sChuXi);
			}
		}
		else
		{
			if (l_nlDate.isRunYue && (l_nlDate.day==Calendar::MonthDaysFun(l_nlDate.year,l_nlDate.month,true)))
			{
				vecNlFtv.push_back(l_sChuXi);
			}
		}
	}
	
	// ==========24节气的判断===========
	int l_iYearIdx = l_glDate.year-1900;
	char l_szMaJQ[10] = {0};
	sprintf(l_szMaJQ, "%04d%02d%02d", l_glDate.year, l_glDate.month, l_glDate.day);
	l_sMatchStr = l_szMaJQ;
	for (int i = 0; i < 24; i++)
	{
		if (l_szMaJQ == g_JieQiFtvDay[l_iYearIdx][i])
		{
			vecJqFtv.push_back(g_JieQiName[i]);
			break;
		}
	}
	
	return vecGlFtv.size() + vecNlFtv.size() + vecJqFtv.size();
	
}


int Festival::DateOfBetweenFestival(int y, int m, int d, string& ymd1, string& ftv1, string& ymd2, string& ftv2)
{
	int l_iYearIdx = y-1900;
	
	int l_szMaJQ = y * 100 * 100 + m * 100 + d;
	
	bool found = false;
	int n1 = 23;
	int n2 = 0;
	for (int k=l_iYearIdx-1; k<=l_iYearIdx+1; k++) {		
		for (int j = 0; j < 24; j++)
		{
			int i = (j+22) % 24;
			string ftv = g_JieQiFtvDay[k][i];
			int n_ftv;
			sscanf(ftv.c_str(), "%d", &n_ftv);
			if (l_szMaJQ < n_ftv)
			{
				n1 = i - 1;
				n2 = i;
				if (n1<0)
					n1=23;
				ymd2 = ftv;
				ftv1 = g_JieQiName[n1];
				ftv2 = g_JieQiName[n2]; 
				found = true;
				break;
			}
			ymd1 = ftv;
		}
		if (found)
			break;
			
	}
	
	
	return 0;
}



int Festival::DateOfFestival(const DateInfo& l_date, FtvList& vecGlFtv, FtvList& vecNlFtv, FtvList& vecJqFtv, bool bGLDate)
{
	return DateOfFestivalForType(1, l_date, vecGlFtv, vecNlFtv, vecJqFtv, bGLDate); 	
}


int Festival::DateOfFestival2(const DateInfo& l_date, FtvList& vecGlFtv, FtvList& vecNlFtv, FtvList& vecJqFtv, bool bGLDate)
{
	return DateOfFestivalForType(2, l_date, vecGlFtv, vecNlFtv, vecJqFtv, bGLDate); 	
}


/************************************************************************************
 * @brief
 *  传入公历的年月，得到这个月几天
 * 
 * @n<b>函数名称</b>: GetGlMonthDays
 * @n@param int y   : 
 * @param   int m   : 
 * @return           
 * @see              
 * @n<b>作者</b>    :檀杨挺
 * @n<b>创建时间</b>: 2010-11-25 18:31:55
 * @version	修改者        时间        描述@n
 * @n		檀杨挺        2010-11-25 
 ************************************************************************************/
int Festival::GetGlMonthDays(int y,int m)
{
	if ( m > 12 || m< 1)
	{
		return 0;
	}
	if(m==2)//2月
	{
		return((((y%4 == 0) && (y%100 != 0)) || (y%400 == 0))? 29: 28);
	}
	else
	{
		return(g_GlMonthDays[m-1]);
	}
}